import React, { useState } from 'react';

import useFetch from '../../../hook/useFetch.js';
import extractVideoId from '../../../utils/extractVideoId.js';

import './TranscriptComponent.css';

const TranscriptComponent = ({youtubeUrl}) => {
  var transcript, videoId = '';

  if (youtubeUrl != '') {
    videoId = extractVideoId(youtubeUrl);
    const {data, isLoading, error} = useFetch(videoId);
    transcript = (data.transcript || []).map(item => item.text ?? '').join(' ');
    console.log('HERE HEREHRERE', transcript);
  } else {
    transcript = 'No transcript yet :)';
  }

  return (
    <div>
      {transcript} 
    </div>
  );
}

export default TranscriptComponent;
