import React, { useState } from 'react';

const Summary = () => {
  const [displayText, setDisplayText] = useState(''); // Initial state is an empty string

  const handleTextChange = (event) => {
    setDisplayText(event.target.value);
  };

  return (
    <div>
      <label>
        Display Text:
        <textarea
          value={displayText}
          onChange={handleTextChange}
          placeholder="Type something..."
        />
      </label>
      <div>
        <h3>Preview:</h3>
        <div className="textbox-preview">
          <p>{displayText}</p>
        </div>
      </div>
    </div>
  );
}

export default Summary;
