import './UrlInput.css';

import React, { useState, useRef } from 'react';
import extractVideoId from '../../utils/extractVideoId.js';

const UrlInput = ({ callback }) => {
  const [url, setUrl] = useState('');
  const [videoId, setVideoId] = useState('');

  const abortControllerRef = useRef(null);

  const fetchTranscript = async (videoId) => {
    var transcript = [];

    abortControllerRef.current?.abort(); // abort request if request already exists
    abortControllerRef.current = new AbortController();

    try {
      const response = await fetch(`/get-transcript/${videoId}`, {
        signal: abortControllerRef.current?.signal,
      }); // will enable us on next request to abort the request if needed 
      transcript = await response.json();
    } catch (error) {
      if (error.name === "AbortError") {
        console.error(error);
        return;
      }
    } finally {
      console.log("done");
    }

    return transcript; 
}

  const handleInputChange = (event) => {
    setUrl(event.target.value);
    console.log("URL:", url);
  };

  const handleSubmit = (event) => {
    event.preventDefault();
    setVideoId(extractVideoId(url)); // get videoId from url and set videoId
    fetchTranscript(videoId)
      .then((transcriptData) => {
        console.log(transcriptData);
        callback(transcriptData);
      })
      .catch((error) => {
        console.error(error);
      })
  };

  return (
    <div className='url-input-container'>
      <div className='url-input'>
        <input
          type='url'
          value={url}
          onChange={handleInputChange}
          placeholder='Enter youtube url here'
        />
        <button onClick={handleSubmit} type='submit'>
          Submit
        </button>
      </div>
    </div>
  );
}

export default UrlInput;
